{
    "name": "用户咨询",
    "description": "用户提供了详细的烹饪配方及其营养成分，并请求调整食材用量以达到特定的营养目标。",
    "steps": [
        {
            "step_number": 1,
            "step_name": "解析问题",
            "actions": [
                "理解用户的配方和当前的营养素含量。",
                "确定需要调整的具体营养素及其调整范围。"
            ]
        },
        {
            "step_number": 2,
            "step_name": "分析现有食材",
            "actions": [
                "列出所有使用的食材及其各自的营养成分。",
                "计算当前配方中各营养素的总量。"
            ]
        },
        {
            "step_number": 3,
            "step_name": "确定调整目标",
            "actions": [
                "明确每个需要调整的营养素的目标范围。",
                "识别哪些营养素需要增加或减少。"
            ]
        },
        {
            "step_number": 4,
            "step_name": "计算调整比例",
            "actions": [
                "根据现有总量与目标，计算各食材的增减比例。",
                "确保调整后仍保持食物的美味和安全性。"
            ]
        },
        {
            "step_number": 5,
            "step_name": "验证调整方案",
            "actions": [
                "检查调整后的营养素是否符合目标范围。",
                "评估调整对整体食材用量的影响，确保合理性。"
            ]
        }
    ],
    "input_format": {
        "食材列表": [],
        "当前用量": {},
        "营养成分数据": {}
    },
    "output_format": {
        "建议用量": {},
        "调整说明": []
    }
}

