{
  "problem": "根据用户提供的营养素调整策略和食材数据，调整食材用量以符合目标范围。",
  "steps": [
    "解析用户提供的食材及其营养素数据。",
    "分析当前食材组合的营养素值。",
    "比较当前值与目标范围，确定需要调整的方向。",
    "确定哪些食材能有效调整目标营养素。",
    "通过增减食材用量来达到目标范围。",
    "确保调整后的食材用量合理且可操作。"
  ],
  "data_analysis": {
    "current_nutrients": {
      "硫胺素": "计算当前硫胺素总和",
      "钙": "当前钙总量为：(鸡蛋98.3g * 0.22) + (香蕉34.8g * 0.03) + ...其他食材",
      "蛋白质": "同理计算其他营养素"
    },
    "adjustment_targets": {
      "硫胺素": "减少0.10毫克",
      "钙": "需在95.90到495.90毫克之间调整",
      "蛋白质": "在-10.82到9.18克之间调整"
    }
  },
  "adjustment_strategy": {
    "减少硫胺素": "减少高硫胺素食材如芝麻籽或鸡蛋的用量",
    "调整钙": "可能减少芝麻籽（高钙）或增加小麦粉（中钙）",
    "蛋白质调整": "减少高蛋白食材如芝麻籽或鸡蛋",
    "其他营养素": "逐一分析并调整"
  },
  "final_adjustments": {
    "鸡蛋": "减少用量以降低硫胺素、蛋白质等",
    "芝麻籽": "可能减少以控制钙和硫胺素",
    "增加香蕉": "若需增加维生素C，但需看目标",
    "调整花生油": "调整脂肪含量，同时影响维生素E和锌"
  },
  "validation": "检查所有调整后的营养素是否在目标范围内，尤其是关键限制如硫胺素减少和钠减少。"
}
